mapper.update:
UPDATE mate_chatroom
SET last_chat_id = #{messageId}
WHERE id = #{roomId};

mapper.select:
select last_chat_id
from mate_chatroom
where id=#{roomId}

select id
from mate_chatroom
where post_id=#{postId} AND guest_id=#{guestId}

select chatroom_name
from mate_chatroom
where id=#{roomId}

select user_id
from mate_user_chatroom
where  not user_id = #{senderId}
  AND chatroom_id=#{roomId}

select id, message_type as messageType, chatroom_id as chatroomId, sender_id as senderId, message, created_at as createdAt
from mate_chat_message
where chatroom_id=#{roomId}

mapper.update:
UPDATE mate_user_chatroom
SET last_join_at = NOW()
WHERE user_id = #{userId} AND chatroom_id=#{roomId};
