mapper.insert:
INSERT INTO spot
VALUES (null, #{contentId}, #{contentTypeId}, #{title}, #{tel}, #{image}, #{homepage}, #{address}
,#{mapX}, #{mapY}, #{areaCode}, #{sigunguCode}, #{overview}, #{infoCenter}, #{openDate}, #{restDate}, #{expguide},
#{usetime}, #{parking})
on duplicate key update content_id = #{contentId}

mapper.update:
